// import css from './ CardInfo.module.css';
import PropTypes from 'prop-types';

export const CardInfo = ({ username, tag, location, avatar, stats }) => {
  return (
    <div className="profile">
      <div className="description">
        <img src={avatar} alt="User avatar" className="avatar" />
        <p className="name">{username}</p>
        <p className="tag">@{tag}</p>
        <p className="location">{location}</p>
      </div>

      <ul className="stats">
        <li>
          <span className="label">{stats.followers}</span>
          <span className="quantity">1000</span>
        </li>
        <li>
          <span className="label">{stats.views}</span>
          <span className="quantity">2000</span>
        </li>
        <li>
          <span className="label">{stats.likes}</span>
          <span className="quantity">3000</span>
        </li>
      </ul>
    </div>
  );
};

CardInfo.propTypes = {
  username: PropTypes.string.isRequired,
  tag: PropTypes.string.isRequired,
  location: PropTypes.string.isRequired,
  avatar: PropTypes.string.isRequired,
  stats: PropTypes.object.isRequired,
};
